// src/UserProgressByRuleTree.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const UserProgressByRuleTree = () => {
  const [progress, setProgress] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.post('http://localhost:5001/api/user-progress', {
          retailer_id: 'ED0538AD-38AF-422F-8450-21FDF1A0FAE2',
          user_id: 'dcd393fe-3ff0-11ef-8048-69e7ac110003',
          ids: ['c45c9b1b-8859-4012-96b8-4f7159b7d1ac'],
          culture: 'en-US'
        });
        setProgress(response.data.payload.rule_trees[0].root.rules['77c4fa9c-faed-47e2-b5b7-bd1b89a2b751']);
      } catch (error) {
        setError(error);
      }
    };

    fetchData();
  }, []);

  if (error) return <div>Error: {error.message}</div>;
  if (!progress) return <div>Loading...</div>;

  const current = Math.round(parseFloat(progress.current));
  const expected = parseFloat(progress.expected);
  const percentage = (current / expected) * 100;

  return (
    <div>
      <h1>User Progress By Rule Tree</h1>
      <div>
        <strong>{current}</strong><br></br>
        points
      </div>
      <div style={{ width: '100%', backgroundColor: '#e0e0df', borderRadius: '2px', marginTop: '10px' }}>
        <div style={{ width: `${percentage}%`, backgroundColor: '#76c7c0', height: '24px', borderRadius: '2px' }}>
          {percentage.toFixed(2)}%
        </div>
      </div>
    </div>
  );
};

export default UserProgressByRuleTree;
